// æ£€æŸ¥ ng conf æ˜¯å¦æœ‰é‡å¤çš„ location entry const fs = require('fs'); const path = require('path'); const ConfEnvDirs = new Set(); ConfEnvDirs.add('dev'); ConfEnvDirs.add('st'); ConfEnvDirs.add('uat'); ConfEnvDirs.add('prd'); const NgconfPath = 'www/ngconf'; let result = 'pass'; countLocationInDir(NgconfPath); if(result !== 'pass'){ process.exit(1);//å‘ bash è¿”å› 1 } /** * * @param rootPath */ function countLocationInDir(rootPath) { if (!fs.existsSync(rootPath)) return; let dirs = fs.readdirSync(rootPath); dirs.forEach(dir => { let envDir = path.join(rootPath, dir); //åªå¤„ç†å››ä¸ªç¯å¢ƒç›®å½•ä¸‹çš„ conf æ–‡ä»¶ï¼Œæ¯ä¸ªç›®å½•ç”¨ä¸€ä¸ª map è®°å½• const LocationEntryMap = new Map();//location entry -> file,line if (!(fs.statSync(envDir).isDirectory() && ConfEnvDirs.has(dir))) { return; } let confFiles = fs.readdirSync(envDir); confFiles.forEach(filename => { let fullPath = path.join(envDir, filename); if (fs.lstatSync(fullPath).isFile() && /[\w\W.].conf$/.test(filename)) { countLocationsInFile(fullPath, LocationEntryMap); } }); }); } /** * * @param confFile * @param countMap */ function countLocationsInFile(confFile, countMap) { let lines = fs.readFileSync(confFile, "utf-8") .split("\n") .filter(Boolean); lines.forEach((line, lineNumber) => { if (line.trim().startsWith("location")) { const arr = line.trim().split(' '); const locationEntry = arr[1]; const entryInfo = `${confFile}, at line:${lineNumber}`; if (countMap.has(locationEntry)) { console.log(`ERROR: duplicate location entry: ${locationEntry}`); console.log(`location 1:${countMap.get(locationEntry)}`); console.log(`location 2:${entryInfo}`); //ä¿®æ”¹ result å˜é‡ result='error'; } else { countMap.set(locationEntry, entryInfo); } } }); }

